import { Component, EventEmitter, Input, Output , OnInit } from '@angular/core';

@Component({
  selector: 'app-default-screen',
  templateUrl: './default-screen.component.html',
  styleUrls: ['./default-screen.component.scss']
})
export class DefaultScreenComponent implements OnInit{
  @Input() lotList : string[][] = []
  @Input() Total : number = 0
  @Output() notify : EventEmitter<string> = new EventEmitter()
  colors = [
    "#F44336", // red
    "#4CAF50", // green
    "#2196F3", // blue
    "#FFC107", // yellow
    "#9C27B0", // purple
    "#673AB7", // deep purple
    "#795548", // brown
    "#607D8B", // blue grey
    "#FF9800", // orange
    "#00BCD4" // cyan
  ];
  ngOnInit(){
    this.lotList.forEach((lot)=>{
      console.log( 'lots '+lot[2]);

      this.Total += Number(lot[2])
    })
    console.log(this.Total);

  }
  OnClick(data : string){
    this.notify.emit(data)
  }
}
<div class="container">
  <div
    *ngFor="let lot of lotList; index as i"
    class="panel"
    (click)="OnClick(lot[1])"
  >
    <div class="pill">
      <span class="colored" [style]="'color : ' + colors[i]">{{ i }}</span>{{ "- " + lot[0] }} total : {{lot[2]}}
    </div>
  </div>
  <div class="panel total">
    <div class="pill total">
      <span class="label">Total : </span>
      <span class="value">{{ Total }}€ TTC</span>
    </div>
  </div>
</div>
