import { Component,OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DpgfService } from 'src/app/Services/dpgf.service';

 type dpgf = {
  _id : string,
  total : string,
  project : string,
  lots : Array<Record<string , string | Array<Record<string , string | Array<Record<string , string>>>>>>
}
@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.scss']
})



export class MainComponent implements OnInit {
  ///// Variables 
  dpgfId : string | null= ''
  DPGF! : dpgf; // dpgf has everything all data
  LotList : Array<Record<string ,string>>=[] //list of lots from dpgf







  ///// constructor
  constructor (private dpgf : DpgfService , private route  : ActivatedRoute, private router : Router){}








  //// methods :
  isArray(arr  : any){return Array.isArray(arr)}
  getIdFromUrl(){
    this.route.paramMap.subscribe((r)=>{
      this.dpgfId = r.get('id')
    })

  }

  InitializeDpgfAndLotList(){
    this.dpgf.getDpgf(this.dpgfId as string)
    .subscribe(
      (dpgf)=>{
        if (typeof(dpgf) == 'number') {
          console.log("error fetching dpgf".toUpperCase());

        } else {
          this.DPGF = dpgf as dpgf
          this.DPGF.lots.forEach((lot)=>{
            this.LotList.push({name: lot['name'] as string,_id :lot["_id"] as string, total : lot["total"] as string })
          })
         console.log(this.DPGF);


        }
      }
    )
  }






  //lifecycle hooks

  ngOnInit() {
    this.getIdFromUrl()
    this.InitializeDpgfAndLotList()   
  }






  ////// Notification methods
  OnNotifyNav(data: Object){
    console.log(JSON.stringify(data)+' was clicked !');

  }
  OnNotifyDefaulrScreen(data: Object){
    console.log(JSON.stringify(data)+' was clicked !');
  }
}
