import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { SousLot } from '../sous-lot/sous-lot.component';

type Lot = {
  id : string,
  total : string,
  name : string,
  sousLots : Array<SousLot>
}

@Component({
  selector: 'app-lot',
  templateUrl: './lot.component.html',
  styleUrls: ['./lot.component.scss']
})
export class LotComponent implements OnInit {

  Total : number = 0

  @Input() Lot :Lot ={
  id : '3423434',
  total : '0',
  name : 'Lot 1',
  sousLots  : [
    {
    id : 'sl11',
    name: 'sous lot 12',
    posts : [
      {
        id : '123', 
        designation : 'whateveer',
        unit : 'm',
        total : '200',
        qte : '10',
        pu : '20',
        tva : ''
      },
      {
        id : '124', 
        designation : 'whateveer',
        unit : 'm',
        total : '20',
        qte : '1',
        pu : '20',
        tva : ''
      }
    ],
    total : '220'
  },
  {
    id : 'sl12',
    name: 'sous lot 13',
    posts : [
      {
        id : '123', 
        designation : 'whateveer',
        unit : 'm',
        total : '200',
        qte : '10',
        pu : '20',
        tva : ''
      },
      {
        id : '124', 
        designation : 'whateveer',
        unit : 'm',
        total : '20',
        qte : '1',
        pu : '20',
        tva : ''
      }
    ],
    total : '220'
  }
]

}
  @Output() notify = new EventEmitter()


///// logic functions
CalcSum(){
  this.Total = 0 
  this.Lot.sousLots.forEach((sl)=>{
    let total = Number(sl.total)
    if (!isNaN(total)) {
      this.Total += total
      this.Lot.total = String(this.Total)
    }
  })
}

FindSousLotByIdAndReplace(replacement : SousLot){
  let index = 0
  let flag = 0
  this.Lot.sousLots.forEach((sl)=>{
    if (sl.id == replacement.id) {
      this.Lot.sousLots[index] = replacement
      flag = 1
    }
    index++
  })
  return flag
}
//////// Lifecycle Hooks

ngOnInit(){
  this.CalcSum()
}

///// event handlers
OnNotify(event : SousLot){
  console.log("Lot comp : ---------------");

  console.log(event);
  if (this.FindSousLotByIdAndReplace(event)==0) {
    this.Lot.sousLots.push(event)
  }
  console.log(' Lot: ++++++++++++++++++');
  console.log(this.Lot);
  this.CalcSum()

}
AddSousLot(){
  this.Lot.sousLots.push({
    id : String(new Date().getTime()),
    name: 'Nouveau Sous-Lot',
    posts : [],
    total : ''
  })
}

}
<div class="test">
        <div class="container">
            <div class="header">
                <input class="name" value="{{Lot.name}}">
                <button type="button" (click)="AddSousLot()">+ Add Section</button>

            </div>
            <div class="main">
                <div class="souslot" *ngFor="let souslot of Lot.sousLots" >
                    <app-sous-lot  *ngIf="Lot.sousLots.length != 0"  [SousLot]="souslot" (notify)="OnNotify($event)"></app-sous-lot>
                </div>
            </div>
            <div class="add">
                <span>total : {{Total}}€</span>
            </div>
        </div>
</div>
