import { Injectable } from '@angular/core';
import {HttpClient, HttpErrorResponse }from '@angular/common/http'
import { Observable ,tap,firstValueFrom} from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  constructor(private http : HttpClient) { }

  Login( data : Record<string,string>){

    const evaluateResp = (resp : Object)=>{
      let response = resp as Record<string,string>
      return [response["token"],"success"]


    }
    const OnError = (err : HttpErrorResponse)=>{
      console.log(err.status);
      if (err.status == 401) {
        return ["Incorrect email or password","error"]
      }
      else if (err.status == 404){
        return ["Url not found Check url Spelling","error"]
      }else if(err.status == 0){
        return ["Couldnt send Request","error"]
      }
      else{
        return ["enternal server error","error"]
      }
    }

    return firstValueFrom(this.http.post("http://127.0.0.1:8080/auth/login",JSON.stringify(data),{headers : {"Content-type" : "application/json"}}))
          .then(evaluateResp)
          .catch(OnError)





  }
  Validate(Object : Record<string,string>) : Promise<boolean>{
    const sendRequest = ()=>{
      return firstValueFrom (this.http.post("http://127.0.0.1:8080/auth/Validate",JSON.stringify(Object),{headers : {"Content-type" : "application/json"}}))

    }
    const validateResponse= (res : Object)=>{
      let record = res as Record<string,string>
      if (record["status"] == "success") {
        return true
      }
      else{
        return false
      }
    }
    const OnError = (err : HttpErrorResponse)=>{
      console.log(err.status);
      return false

    }
    return sendRequest()
    .then(validateResponse)
    .catch(OnError)

  }
  Register(Object : Record<string,string>){
    const sendRequest = ()=>{
      return firstValueFrom(this.http.post("http://127.0.0.1:8080/auth/register",JSON.stringify(Object),{headers : { 'content-type' : 'application/json'}}))
    }
    const validateResponse = (Object : Object)=>{
      let record = Object as Record<string,string>
      if (record['status'] == 'success') {
        return [record['token'],'success']
      }else if (record["error"] == "user already exists"){   //to be changed to record['status'] because inconsistent
        throw new Error("1")
      }
      else {
        throw new Error("2")
      }
    }
    const OnError = (err : Error)=>{
      if (err.message == '1') {
        return ['user exists','error']
      } else {
        return ['server error','error']
      }
    }
    return sendRequest()
    .then(validateResponse)
    .catch(OnError)

  }
}
