import { Component, Input, Output,EventEmitter,OnInit } from '@angular/core';
import { FormArray, FormBuilder,Validators } from '@angular/forms';


@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.scss']
})
export class FormComponent implements OnInit {
  //Input
  @Input() fieldList: string[][] =[]
  @Input() title: string =''
  @Input() button: string =''
  //Outut
  @Output() notify = new EventEmitter() 
  //internal
  form = this.formbuilder.array([])
  emptyFields = false
//// methods
  constructor(private formbuilder : FormBuilder){}
  ngOnInit(){
    this.fieldList.forEach(()=>{
      let newcontrol = this.formbuilder.control('',{validators : [Validators.required]})
      this.form.push(newcontrol)
    })
  }
  OnSubmit(){
    if (this.form.valid) {

      let newRecord : Record<string , string>  = {}
      let i =0
      this.fieldList.forEach((field)=>{
        newRecord[field[0].toLowerCase()] = this.form.value.at(i) as string
        i++ 
      })
      this.notify.emit(newRecord)
    }else {
      this.emptyFields = true
    }
  }
}
<form (submit)="OnSubmit() ">
    <h3>{{title}}</h3>
    <div *ngFor="let field of fieldList; index as  i">
        <label for="{{field[0]}}">{{field[0]}} </label>
        <input type="{{field[1]}}" name="{{field[0]}}" [formControl]="form.controls[i]" />
    </div>
    <p *ngIf="emptyFields" style="color:red">All fields are required</p>
    <button type="submit">{{button}}</button>
</form>
