import { Injectable } from '@angular/core';
import {HttpClient, HttpErrorResponse }from '@angular/common/http'
import { Observable ,tap,firstValueFrom} from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  constructor(private http : HttpClient) { }

  Login( data : Record<string,string>){

    const evaluateResp = (resp : Object)=>{
      let response = resp as Record<string,string>
      return [response["token"],"success"]


    }
    const OnError = (err : HttpErrorResponse)=>{
      //console.log(err.status);
      if (err.status == 401) {
        return ["Incorrect email or password","error"]
      }
      else if (err.status == 404){
        return ["Url not found Check url Spelling","error"]
      }else {
        return ["enternal server error","error"]
      }
    }

    return firstValueFrom(this.http.post("http://127.0.0.1:5050/auth/login",JSON.stringify(data),{headers : {"Content-type" : "application/json"}}))
    .then(evaluateResp)
    .catch(OnError)





  }
}
