import { Component,OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DpgfService } from 'src/app/Services/dpgf.service';

type lotObject  ={
  _id : string;
  name :string;
  total : string;
}

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.scss']
})
export class MainComponent implements OnInit {
  LotList :string[][] = []
  dpgfId : string | null= ''
  DPGF? : {
    _id : string,
    total : string,
    project : string,
    lots : Array<Record<string , string | Array<Record<string , string | Array<Record<string , string>>>>>>
  } ;

  constructor (private dpgf : DpgfService , private route  : ActivatedRoute, private router : Router){}
  ngOnInit() {
    this.route.paramMap.subscribe((r)=>{
      this.dpgfId = r.get('id')
    })
    this.dpgf.getListOfLotsByDpgfId(this.dpgfId).subscribe((res)=>{
      if (res == 400 || res == 500) {
        this.router.navigate(['/home/projects/list'])
      } else {
        let record = res as  {lots : Array<lotObject>}
        record.lots.forEach((o)=>{
          this.LotList.push([o.name,o._id ,o.total])
        })
      }
    })

  }
  OnNotify(data: string){
    console.log(data+' was clicked !');

  }
}
