import { Component, EventEmitter, Input, OnChanges, OnInit, Output, SimpleChanges  } from '@angular/core';

@Component({
  selector: 'app-default-screen',
  templateUrl: './default-screen.component.html',
  styleUrls: ['./default-screen.component.scss']
})
export class DefaultScreenComponent implements OnChanges , OnInit{
  @Input() lotList : string[][] = []
  @Input() Total : number = 0
  @Output() notify : EventEmitter<string> = new EventEmitter()
  colors = [
    "#F44336", // red
    "#4CAF50", // green
    "#2196F3", // blue
    "#FFC107", // yellow
    "#9C27B0", // purple
    "#673AB7", // deep purple
    "#795548", // brown
    "#607D8B", // blue grey
    "#FF9800", // orange
    "#00BCD4" // cyan
  ];

  ngOnChanges(changes : SimpleChanges){

    if (changes['lotList']) {
      console.log('cahnge happened');

      console.log(this.lotList[1]);

    }

  }
  async sleep(ms :number) {
    return new Promise((resolve)=>setTimeout(resolve, ms))
  }

  ngOnInit(){
    this.sleep(100).then(()=>{
      this.lotList.forEach((lot)=>{
        this.Total += Number(lot[2])
      })
    })

  }
  OnClick(data : string){
    this.notify.emit(data)
  }
}
<div class="container" *ngIf="lotList">
  <div
    *ngFor="let lot of lotList; index as i"
    class="panel"
    (click)="OnClick(lot[1])"
  >
    <div class="pill">
      <span><span class="colored" [style]="'color : ' + colors[i]">{{ i }}</span>{{ "- " + lot[0] }}</span> 
       <span class="value"> {{ lot[2] }}</span>
    </div>
  </div>
  <div class="panel total">
    <div class="pill total">
      <span class="label">Total : </span>
      <span class="value">{{ Total }}€ TTC</span>
    </div>
  </div>
</div>
