import { Component, EventEmitter, Input, Output } from '@angular/core';
import { SousLot } from '../sous-lot/sous-lot.component';

type Lot = {
  id : string,
  total : string,
  name : string,
  sousLots : Array<SousLot>
}

@Component({
  selector: 'app-lot',
  templateUrl: './lot.component.html',
  styleUrls: ['./lot.component.scss']
})
export class LotComponent {
 @Input() Lot :Lot ={
  id : '3423434',
  total : '0',
  name : 'Lot 1',
  sousLots  : [
    {
    id : '',
    name: 'sous lot 12',
    posts : [
      {
        id : '123', 
        designation : 'whateveer',
        unit : 'm',
        total : '200',
        qte : '10',
        pu : '20',
        tva : ''
      },
      {
        id : '124', 
        designation : 'whateveer',
        unit : 'm',
        total : '20',
        qte : '1',
        pu : '20',
        tva : ''
      }
    ],
    total : ''
  },
  {
    id : '',
    name: 'sous lot 13',
    posts : [
      {
        id : '123', 
        designation : 'whateveer',
        unit : 'm',
        total : '200',
        qte : '10',
        pu : '20',
        tva : ''
      },
      {
        id : '124', 
        designation : 'whateveer',
        unit : 'm',
        total : '20',
        qte : '1',
        pu : '20',
        tva : ''
      }
    ],
    total : ''
  }
]

}
@Output() notify = new EventEmitter()

OnNotify(event : SousLot){

}

}
