import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';

@Component({
  selector: 'app-sous-lot',
  templateUrl: './sous-lot.component.html',
  styleUrls: ['./sous-lot.component.scss']
})
export class SousLotComponent implements OnInit {
  @Input() SousLot = {
    id : '',
    posts : [
      {
        id : '123', 
        designation : 'whateveer',
        unit : 'm',
        total : '200',
        qte : '10',
        pu : '20',
        tva : ''
      },
      {
        id : '124', 
        designation : 'whateveer',
        unit : 'm',
        total : '20',
        qte : '1',
        pu : '20',
        tva : ''
      }
    ]
  }
  @Output() notify = new EventEmitter()

  NotEmpty(){
   return Array.isArray(this.SousLot.posts ) && this.SousLot.posts.length != 0 
  }
  ngOnInit(){

  }
  OnNotify(data : Object){
    console.log("from sous-Lot");
    console.log(data);


  }

}
