import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { catchError, map, of } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class DpgfService {

  url = 'http://127.0.0.1:5050/dpgf/'
  token = localStorage.getItem('TOKEN')

  constructor(private http : HttpClient) { }


  getListOfLotsByDpgfId (id : string | null) {
    return this.http.post(this.url + 'getListOfLots',{id},{headers : {'content-type' : 'application/json' , 'authorization' : this.token as string}})
    .pipe(
      map((res)=>{
        let record  = res as  {lots : Array<Record<string,string>>}
        return record
      }),
      catchError((err : HttpErrorResponse)=>{
        console.log(err.status);
        return of(err.status)

      })
    )
   }
}
