import { Component } from '@angular/core';

@Component({
  selector: 'app-temp',
  templateUrl: './temp.component.html',
  styleUrls: ['./temp.component.scss']
})
export class TempComponent {
  timesToRepeat = [1,2,3,4,5]
  OnClick(){
    let n=  this.timesToRepeat.length
    n++
    this.timesToRepeat.push(n)
  }
}
<div style="display: flex;flex-direction: row;">

    <div>
    <div *ngFor="let o of timesToRepeat">hello {{o}}</div>
    </div>
    <button style="height: 30px;" (click)="OnClick()" >click me</button>
</div>
